def.body:
if @deleted
  return
end
@deleted = true
GRM.with_error_check("args_delete") do LibGRM.args_delete(@ptr) end
@references.clear

plot = Plot.new
configured_plot = yield(plot)
@plots << configured_plot
@dirty = true
self

if @created
  GRM.with_error_check("switch") do LibGRM.switch(@plot_id) end
  GRM.with_error_check("clear") do LibGRM.clear end
end

@plots.clear
@created = false
@dirty = true
self

@kind = "contour"
if m
  @m = m
end
if n
  @n = n
end
self

@kind = "contourf"
if m
  @m = m
end
if n
  @n = n
end
self

if (x.size != y.size) || (x.size != z.size)
  raise(ArgumentError.new("x, y, and z arrays must have the same size"))
end
@x_data = x.map(&.to_f64)
@y_data = y.map(&.to_f64)
@z_data = z.map(&.to_f64)
self

if x.size != y.size
  raise(ArgumentError.new("x and y arrays must have the same size"))
end
@x_data = x.map(&.to_f64)
@y_data = y.map(&.to_f64)
self

@kind = "heatmap"
if m
  @m = m
end
if n
  @n = n
end
self

@kind = "shade"
if m
  @m = m
end
if n
  @n = n
end
self

@kind = "surface"
if m
  @m = m
end
if n
  @n = n
end
self
