def.body:
plot = Plot.new
configured_plot = yield(plot)
@plots << configured_plot
@dirty = true
self

if @created
  LibGRM.switch(@plot_id)
  LibGRM.clear
end

@plots.clear
@created = false
@dirty = true
self

@kind = "contour"
if m
  @m = m
end
if n
  @n = n
end
self

@kind = "surface"
if m
  @m = m
end
if n
  @n = n
end
self
